mapper.update:
update analog_output
   set is_new = 1
     , scan_value = #{scanValue}
     , scan_time = now()
 where tag_id = #{tagId}

update analog_output
   set is_new = 0
 where tag_id = #{tagId}

update analog_output
   set percent = #{percent}
     , slope = #{slope}
     , is_new = 1
     , scan_value = #{scanValue}
     , scan_time = coalesce(#{scanTime},now())
     , prev_value = #{prevValue}
     , prev_time = #{prevTime}
     , last_hist_value = #{lastHistValue}
     , last_hist_time = #{lastHistTime}
 where tag_id = #{tagId}

update analog_output
   set zero_value = #{zeroValue}
     , max_value = #{maxValue}
     , is_new = 0
     , hist_type_code = #{histTypeCode}
     , percent = #{percent}
     , unit_id = #{unitId}
 where tag_id = #{tagId}
