mapper.select:
select count(*) from shipment_item 
 where active not in ('C','D')
   and shipment_id=#{param1} and carrier_id=#{param2}

mapper.update:
update shipment
   set customer_id = #{customerId}
     , purchase = #{purchase}
     , exp_date = #{expDate}
     , act_date = #{actDate}
 where shipment_id = #{shipmentId}

update shipment_item
   set active = #{active}
     , content_cd = #{contentCd}
     , carrier_id = #{carrierId}
     , exp_volume_min = #{expVolumeMin}
     , exp_volume_max = #{expVolumeMax}
     , act_volume = #{actVolume}
     , transfer_id = #{transferId}
     , station_id = #{stationId}
 where shipment_id = #{shipmentId}
   and item_no = #{itemNo}

mapper.insert:
insert shipment_item
      ( shipment_id, item_no, carrier_id, active, content_cd
      , exp_volume_min, exp_volume_max, act_volume
      , transfer_id, station_id )
values( #{shipmentId}, #{itemNo}, #{carrierId}, #{active}, #{contentCd}
      , #{expVolumeMin}, #{expVolumeMax}, #{actVolume}
      , #{transferId}, #{stationId} )
on duplicate key update
        active = #{active}
      , content_cd = #{contentCd}
      , carrier_id = #{carrierId}
      , exp_volume_min = #{expVolumeMin}
      , exp_volume_max = #{expVolumeMax}
      , act_volume = #{actVolume}
      , transfer_id = #{transferId}
      , station_id = #{stationId}

mapper.update:
update shipment_item
   set active = #{code}
 where transfer_id = #{id}
