mapper.select:
select value
  from digital_output do 
  join off_on_vw vv 
 where do.tag_id = #{id}
   and vv.name = #{name}

mapper.update:
update reference_code
   set name = #{name}
     , code = #{code}
     , value = #{value}
     , description = #{description}
     , active = #{active}
 where id= #{id}

mapper.insert:
insert reference_code(category, name, code, value, description, active) 
values (#{category}, #{name}, #{code}, #{value}, #{description}, #{active})
on duplicate key update
         category = #{category}
       , name = #{name}
       , code = #{code}
       , value = #{value}
       , description = #{description}
       , active = #{active}
