mapper.select:
select t.id, t.name from tag t where t.tag_type_code in
#{parm}
order by 2

mapper.update:
update tag
   set name = #{name}
     , description = #{description}
     , tag_type_code = #{tagTypeCode}
     , misc = #{misc}
     , c1_lat = #{c1Lat}
     , c1_long = #{c1Long}
     , c2_lat = #{c2Lat}
     , c2_long = #{c2Long}
     , active = #{active}
 where id = #{id}

update rel_tag_tag
   set parent_tag_id = #{parentTagId}
     , child_tag_id = #{childTagId}
     , code = #{code}
 where id = #{id}

mapper.insert:
insert tag
       ( name, description, tag_type_code, misc
       , c1_lat, c1_long, c2_lat, c2_long, active )
values( #{name}, #{description}, #{tagTypeCode}, #{misc}
      , #{c1Lat}, #{c1Long}, #{c2Lat}, #{c2Long}, #{active})

  SELECT LAST_INSERT_ID();
