mapper.update:
update vertex
   set seq_no = #{seqNo}
     , latitude = #{latitude}
     , longitude = #{longitude}
 where tag_id= #{tagId}

mapper.insert:
insert vertex( tag_id, seq_no, latitude, longitude) 
values (#{tagId}, #{seqNo}, #{latitude}, #{longitude})
on duplicate key update
       tag_id = #{tagId}
     , seq_no = #{seqNo}
     , latitude = #{latitude}
     , longitude = #{longitude}
